// @flow
import React from 'react';
import { Card, CardBody } from 'reactstrap';
import { Bar, withResponsiveness } from 'britecharts-react';

const ResponsiveBarChart = withResponsiveness(Bar);

// simple bar chart
const BarChart = (props) => {
    const barChartData = [
        { name: 'Mon', value: props.jan },
        { name: 'Tue', value: props.feb },
        { name: 'Wed', value: props.mar },
        { name: 'Thu', value: props.apr },
        { name: 'Fri', value: props.may },
        { name: 'Sat', value: props.jun },
        { name: 'Sun', value: props.jul },
        { name: 'Sun', value: props.aug },
        { name: 'Sun', value: props.seb },
        { name: 'Sun', value: props.oct },
        { name: 'Sun', value: props.nov },
        { name: 'Sun', value: props.dec },
    ];

    return (
        <Card>
            <CardBody>
                <h4 className="header-title mb-4">Bar Chart</h4>
                <div className="bar-container" style={chartContainerStyle}>
                    <ResponsiveBarChart
                        isAnimated={false}
                        data={barChartData}
                        isHorizontal={false}
                        height={500}
                        betweenBarsPadding={0.5}
                        colorSchema={['#39afd1']}
                        margin={{ top: 10, left: 55, bottom: 20, right: 10 }}
                    />
                </div>
            </CardBody>
        </Card>
    );
};

export default BarChart;
