import React, {useState, useEffect} from "react";
import LineChart from "./LineChart";

const Properties =()=>{
    const [inputs, setInputs] = useState({
        area: '',
        tilt: '',
        azimuth:''
    });
    const { area, tilt, azimuth } = inputs;

    const onChange = (e) => {
        const { name, value }  = e.target;
        setInputs({
        ...inputs,
        [name]: value,
        });
    };

    const onReset = (e) => {
        e.preventDefault()
        const resetInputs ={
             area: '',
            tilt: '',
            azimuth:''
        }
        setInputs(resetInputs)
      };
    return(
        <div>
      <form>
        <input name="area"
          placeholder="넓이를 입력하세요"
          onChange={onChange}
          value={area}
        /><br/><br/>
          <input name="tilt"
          placeholder="경사각을 입력하세요"
          onChange={onChange}
          value={tilt}
        /><br/><br/>
          <input name="azimuth"
          placeholder="방위각을 입력하세요"
          onChange={onChange}
          value={azimuth}
        /><br/>
        <button onClick={onReset}>검색</button>
      </form>
            </div>
    );
}
export default Properties
