import React, {useState, useEffect} from "react";
import { Row, Col } from 'reactstrap';
import LineChart from "./LineChart";

const Properties =()=>{
    const [af,setAf] = useState(0)
    const [inputs, setInputs] = useState({
        area: '',
        tilt: '',
        azimuth:''
    });
    const { area, tilt, azimuth } = inputs;

    const onChange = (e) => {
        const { name, value }  = e.target;
        setInputs({
        ...inputs,
        [name]: value,
        });
    };

    const onReset = (e) => {
        e.preventDefault()
        const resetInputs ={
             area: '',
            tilt: '',
            azimuth:''
        }
        setInputs(resetInputs)
      };
    const fsad = (e) =>{
        e.preventDefault()
        if(af === 1){
            setAf(0)
        }
        else{
            setAf(1)
        }
    }
    useEffect(()=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({area:inputs.area, tilt:inputs.tilt, azimuth: inputs.azimuth} )
        };
        fetch('http://127.0.0.1:5000/param', requestOptions)
            .then((response) => {
                response.json().then(function(data) {
                    console.log(data)
                // json형식 데이터에 접근할 때는 forEach를 사용한다.
            })
        })
    },[af])
    return(
        <Row>
            <Col>
                <div>
                    <form>
                        <input name="area"
                          placeholder="넓이를 입력하세요"
                          onChange={onChange}
                          value={area}
                        /><br/><br/>
                          <input name="tilt"
                          placeholder="경사각을 입력하세요"
                          onChange={onChange}
                          value={tilt}
                        /><br/><br/>
                          <input name="azimuth"
                          placeholder="방위각을 입력하세요"
                          onChange={onChange}
                          value={azimuth}
                        /><br/>
                        <button onClick={fsad}>검색</button>
                        <button onClick={onReset}>Reset</button>
                      </form>
                    </div>
                </Col>
            </Row>
    );
}
export default Properties
