    /*:: toggle: () => void */
    toggle() {
        this.setState(prevState => ({
            toggleMultiple: !prevState.toggleMultiple,
        }));
    }

    /**
     * show/hide nested modal
     */
    /*:: toggle: () => void */
    toggleNested = () => {
