    const [inputs, setInputs] = useState({
        area: '',
        tilt: '',
        azimuth:''
    });
    const { area, tilt, azimuth } = inputs;

    const onChange = (e) => {
        const { name, value }  = e.target;
        setInputs({
        ...inputs,
        [name]: value,
