    const expandRow = {
        renderer: row => (
            <div>
                <p className="mt-2">{`Hello ${row.name}`}</p>
                <p>You can render anything here, also you can add additional data on every row object</p>
                <p>expandRow.renderer callback will pass the origin row object to you</p>
            </div>
        ),
        showExpandColumn: true,
        onlyOneExpanding: true,
        expandHeaderColumnRenderer: ({ isAnyExpands }) => {
            return isAnyExpands ? <i className="dripicons-minus" /> : <i className="dripicons-plus" />;
        },
        expandColumnRenderer: ({ expanded }) => {
            return expanded ? <i className="dripicons-minus" /> : <i className="dripicons-plus" />;
        },
    };

    return (
        <Card>
            <CardBody>
                <h4 className="header-title">Expand Row</h4>
                <p className="text-muted font-14 mb-4">Expand row to see more additional details</p>

                <BootstrapTable
                    bootstrap4
                    keyField="id"
                    bordered={false}
                    data={records}
                    columns={columns}
                    pagination={paginationFactory({ sizePerPage: 5 })}
