                        }))
                        .map((column, idx) => (
                            <DropdownItem key={idx} toggle={false}>
                                <CustomInput
                                    type="checkbox"
                                    id={`col-${column.text}`}
                                    label={column.text}
                                    defaultChecked={column.toggle}
                                    onClick={() => {
                                        return onColumnToggle(column.dataField);
                                    }}
                                />
                            </DropdownItem>
                        ))}
                </DropdownMenu>
            </UncontrolledDropdown>
        </React.Fragment>
    );
};

